<template>  <div id="admininstration-sidebar">    <div id="admin-header">      <div class="d-flex justify-content-center">        <img id="admin-company-logo" alt="" :src="adminCompanyLogo" />      </div>      <p id="admin-title">{{ adminCompany }}</p>      <div class="row">        <div class="col-md-6">          <h3 class="text-center text-primary">            <Fa class="" :icon="['fas', 'user-tie']" />            {{ workerCount }}          </h3>          <p class="admin-label">Employees</p>        </div>        <div class="col-md-6">          <h3 class="text-center text-primary">            <Fa class="" :icon="['fas', 'network-wired']" />            {{ workingDeviceCount }}          </h3>          <p class="admin-label">Departments</p>        </div>      </div>      <hr class="mb-1" />    </div>    <div id="admin-footer">      <simplebar data-simplebar-auto-hide="false" class="h-100">        <ul class="admin-menu">          <li>            <a class="menu-title ml-1">Company structure settings</a>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-algorithm' }">              <Fa                :icon="['fas', 'project-diagram']"                fixed-width                size="lg"                class="menu-item-icon"              />              Algorithm            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-function' }">              <Fa                :icon="['fas', 'function']"                fixed-width                size="lg"                class="menu-item-icon"              />              Function            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-employee' }">              <Fa                :icon="['fas', 'user-tie']"                fixed-width                size="lg"                class="menu-item-icon"              />              Employee            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-rules' }">              <Fa                :icon="['fas', 'gavel']"                fixed-width                size="lg"                class="menu-item-icon"              />              Rules            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-salary' }">              <Fa                :icon="['fas', 'sack-dollar']"                fixed-width                size="lg"                class="menu-item-icon"              />              Salary            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-job' }">              <Fa                :icon="['fas', 'briefcase']"                fixed-width                size="lg"                class="menu-item-icon"              />              Job Title            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-department' }">              <Fa                :icon="['fas', 'network-wired']"                fixed-width                size="lg"                class="menu-item-icon"              />              Department            </router-link>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'stake-holders' }">              <Fa                :icon="['far', 'user-friends']"                fixed-width                size="lg"                class="menu-item-icon"              />              Stakeholders management            </router-link>          </li>          <li>            <a class="menu-title ml-1">Permission control</a>          </li>          <li>            <router-link class="menu-item" :to="{ name: 'company-permission' }">              <Fa                :icon="['fas', 'unlock-alt']"                fixed-width                size="lg"                class="menu-item-icon"              />              Permission template            </router-link>          </li>        </ul>      </simplebar>    </div>  </div></template><script>import simplebar from 'simplebar-vue';export default {  name: 'Sidebar',  components: {    simplebar,  },  data() {    return {      adminCompanyLogo: null,      adminCompany: null,      workerCount: 0,      workingDeviceCount: 0,    };  },  mounted() {    this.setCompanyInfo();  },  methods: {    setCompanyInfo() {      const company = this.$store.getters.company;      this.adminCompanyLogo = company.logo;      this.adminCompany = company.name;      this.workingDeviceCount = company.department_count;      let count = 0;      for (const emp of company.employees) {        if (emp.is_active === '1') {          count += 1;        }      }      this.workerCount = count;    },  },};</script>