<template>  <div>    <b-table      striped      :hover="true"      :items="tableData"      :fields="tableField"      :responsive="true"    >      <template #cell(partner__name)="data">        <a>{{ data.item.partner__name }} {{ data.item.partner__last_name }}</a>      </template>      <template #cell(is_active)="data">        <b-badge v-if="data.item.is_active === '1'" variant="success">          Active        </b-badge>        <b-badge v-else variant="danger">Inactive</b-badge>      </template>      <template #cell(action)="data">        <div class="d-flex show-cell">          <a            v-if="permission('empl_upd') && data.item.is_active === '1'"            class="cursor-pointer mr-2"            @click="editEmployee(data.item)"          >            <h5>              <Fa :icon="['fas', 'cog']" />            </h5>          </a>          <a            v-if="permission('empl_del') && data.item.is_active === '1'"            class="cursor-pointer"            @click="deleteEmployee(data.item)"          >            <h5>              <Fa :icon="['fas', 'trash']" />            </h5>          </a>        </div>      </template>    </b-table>  </div></template><script>export default {  name: 'EmployeeTable',  props: {    tableData: {      type: Array,    },  },  data() {    return {      tableField: [        {          key: 'partner__name',          label: 'Name',        },        {          key: 'partner__email',          label: 'E-mail',        },        {          key: 'job_title__name',          label: 'Job Title',        },        {          key: 'department__name',          label: 'Department',        },        {          key: 'is_active',          label: 'State',        },        {          key: 'action',          label: 'Action',        },      ],    };  },  methods: {    deleteEmployee(employee) {      this.$emit('delete', employee);    },    editEmployee(employee) {      this.$emit('edit', employee);    },  },};</script>