<template>  <b-modal id="function-modal" v-b-modal.modal-center hide-footer>    <h4 class="text-center">Function added</h4>    <div class="d-flex justify-content-center">      <b-button        class="mt-4 mb-4 pr-5 pl-5"        variant="deep-blue"        @click="hideModal"      >        OK      </b-button>    </div>  </b-modal></template><script>export default {  name: 'FunctionModal',  methods: {    hideModal() {      this.$root.$emit('bv::hide::modal', 'function-modal');      this.$emit('reload', true);    },  },};</script>