<template>  <div class="mt-4 mb-5">    <div class="d-flex justify-content-between align-items-center mb-3">      <h4 class="font-weight-bold">Constitution</h4>      <b-button        v-if="permission('rule_crt')"        v-b-toggle.rule-create        variant="deep-blue"      >        <Fa :icon="['fas', 'plus']" />        Create constitution      </b-button>    </div>    <div class="row">      <div v-for="(rule, index) of rules" :key="index" class="col-lg-3 mb-4">        <div class="rule-item">          <div>            <h5 class="font-weight-bold mb-2">              <router-link                :to="{                  name: 'company-rules-detail',                  params: { id: rule.id },                }"              >                {{ rule.name }}              </router-link>            </h5>            <p class="mb-1 text-black-50">{{ rule.number }}</p>            <p class="mb-1 text-black-50">Created: {{ rule.date }}</p>            <p class="mb-1 text-black-50">Version: {{ rule.version }}</p>            <p class="mb-3">{{ rule.description }}</p>          </div>          <b-button            variant="primary"            class="rule-btn"            :to="{              name: 'company-rules-detail',              params: { id: rule.id },            }"          >            Detail <Fa :icon="['fas', 'caret-right']" />          </b-button>        </div>      </div>    </div>  </div></template><script>export default {  name: 'ConstitutionList',  props: {    rules: {      type: Array,      default() {        return [];      },    },  },};</script>