<template>  <div>    <b-sidebar      id="rule-create"      title="New constitution"      class="sidebar-md"      right      shadow      backdrop      lazy    >      <div class="p-4">        <ContentHeader>          <template #text>            <h4 class="font-weight-bold mb-3">Invite your colleagues!</h4>            <p>              Please input required information below to invite your colleague              to TUS D+ Platform, where you can work as an amazing team. :)            </p>          </template>          <template #image>            <img              src="@/assets/images/administration/add-employee.svg"              class="content-header-image"              alt=""            />          </template>        </ContentHeader>        <b-form>          <b-form-group label="Rule name">            <b-form-input v-model="name" type="text"></b-form-input>            <span v-if="!$v.name.required && formSubmit" class="text-danger">              Name is required            </span>          </b-form-group>          <b-form-group label="Confirm date">            <b-form-input v-model="date" type="date"></b-form-input>            <span v-if="!$v.date.required && formSubmit" class="text-danger">              Date is required            </span>          </b-form-group>          <b-form-group label="Confirm number">            <b-form-input v-model="number" type="text"></b-form-input>            <span v-if="!$v.number.required && formSubmit" class="text-danger">              Confirm number is required            </span>          </b-form-group>          <b-form-group label="Version">            <b-form-input v-model="version" type="number"></b-form-input>            <span v-if="!$v.version.required && formSubmit" class="text-danger">              Version is required            </span>          </b-form-group>          <b-form-group label="Description">            <b-form-textarea              v-model="description"              spellcheck="false"            ></b-form-textarea>            <span              v-if="!$v.description.required && formSubmit"              class="text-danger"            >              Description is required            </span>          </b-form-group>        </b-form>      </div>      <template #footer>        <div class="m-3 p-2">          <b-button            type="submit"            variant="deep-blue"            block            size="lg"            @click="createRule"            >Create          </b-button>        </div>      </template>    </b-sidebar>    <b-modal id="rule-msgbox" centered>      <h4 class="text-center">Rule created</h4>      <template #modal-footer="{ ok }">        <div class="w-100 d-flex justify-content-center">          <b-button variant="deep-blue" class="pr-5 pl-5 mb-3" @click="ok()">            OK          </b-button>        </div>      </template>    </b-modal>  </div></template><script>import ContentHeader from '@/components/admininstration/ContentHeader';import { required, maxLength } from 'vuelidate/lib/validators';import axios from 'axios';export default {  name: 'ConstitutionCreate',  components: { ContentHeader },  data() {    return {      formSubmit: false,      name: null,      date: null,      version: 1,      description: null,      number: null,      organization: this.$store.getters.activeCompany,    };  },  validations: {    name: { required },    date: { required },    version: { required },    description: { required, maxLength: maxLength(255) },    number: { required },  },  methods: {    createRule() {      this.formSubmit = true;      this.$v.$touch();      if (!this.$v.$invalid) {        const payload = {          name: this.name,          date: this.date,          version: this.version,          description: this.description,          number: this.number,          organization: this.organization,        };        const url = '/pages/company_builder/rules/detail/';        axios.post(url, payload).then(() => {          this.$root.$emit('bv::toggle::collapse', 'rule-create');          this.$bvModal.show('rule-msgbox');          this.$emit('reload', true);        });      }    },  },};</script>