<template>  <b-sidebar    id="rule-edit"    title="Edit rule"    class="sidebar-md"    right    shadow    backdrop    lazy  >    <div class="p-4">      <p class="mb-3">        Created by: {{ constitutionData.partner__name }}        {{ constitutionData.partner__last_name }}      </p>      <b-form>        <b-form-group label="Rule name">          <b-form-input v-model="ruleName" type="text"></b-form-input>        </b-form-group>        <b-form-group label="Confirm number">          <b-form-input v-model="ruleNumber" type="text"></b-form-input>        </b-form-group>        <b-form-group label="Confirm date">          <b-form-input v-model="ruleDate" type="date"></b-form-input>        </b-form-group>        <b-form-group label="Version">          <b-form-input v-model="ruleVersion" type="number"></b-form-input>        </b-form-group>        <b-form-group label="Description">          <b-form-textarea v-model="description" rows="3"></b-form-textarea>        </b-form-group>      </b-form>    </div>    <template #footer>      <div class="m-3 p-2">        <b-button          type="submit"          variant="deep-blue"          block          size="lg"          @click="editRule"          >Save        </b-button>      </div>    </template>  </b-sidebar></template><script>import { required } from 'vuelidate/lib/validators';import axios from 'axios';export default {  name: 'ConstitutionEdit',  props: {    constitutionData: {      type: Object,      required: true,    },  },  data() {    return {      formSubmit: false,      ruleName: this.constitutionData.name,      ruleNumber: this.constitutionData.number,      ruleDate: this.constitutionData.date,      ruleVersion: this.constitutionData.version,      organization: this.$store.getters.activeCompany,      ruleId: this.constitutionData.id,      description: this.constitutionData.description,    };  },  validations: {    ruleName: { required },    ruleNumber: { required },    ruleDate: { required },    ruleVersion: { required },  },  methods: {    editRule() {      this.formSubmit = true;      this.$v.$touch();      if (!this.$v.$invalid) {        const payload = {          rule_id: this.ruleId,          name: this.ruleName,          date: this.ruleDate,          version: this.ruleVersion,          number: this.ruleNumber,          organization: this.organization,          description: this.description,        };        const url = `/pages/company_builder/rules/detail/?id=${this.organization}`;        axios.put(url, payload).then(() => {          this.$root.$emit('bv::toggle::collapse', 'rule-edit');          this.$emit('reload', true);        });      }    },  },};</script>