<template>  <div class="col-lg-4 mb-4">    <div class="salary-card">      <div v-if="permission('slry_upd')" class="salary-category-edit">        <Fa          class="salary-category-edit-icon ml-2"          :icon="['fas', 'cog']"          @click="editSalary(salary)"        />      </div>      <h5 class="font-weight-bold text-primary mb-3">{{ salary.name }}</h5>      <p class="mb-2">        <span class="font-weight-bold">Salary:</span>        {{ salary.base_salary }}      </p>      <p class="font-weight-bold">Salary structure:</p>      <p v-for="structure of salary.salary_structure" :key="structure.id">        {{ structure.name }}: {{ structure.percent }}%        <span v-if="structure.salary_type_name">          /{{ structure.salary_type_name }}/        </span>      </p>    </div>  </div></template><script>export default {  name: 'SalaryCategory',  props: {    salary: {      type: [Object],    },  },  methods: {    editSalary(salary) {      this.$emit('edit', salary);    },  },};</script>