       <!--              v-model="json_data[indx].menu_items[index].data"-->        <!--              :options="cols.options"-->        <!--              class="custom-input"-->        <!--              disabled-->        <!--              :required="cols.required"-->        <!--            ></b-form-select>-->        <!--          </b-col>-->        <!--        </b-form-group>-->        <!--                    </b-col>-->        <!--                  </b-form-row> </b-col-->        <!--              ></b-tab>-->        <!--            </b-tabs>-->        <!--          </b-col>-->        <!--        </b-row>-->      </b-col>      <template #footer="{ hide }">        <div cols="12" class="my-3 d-flex">          <b-col cols="10">            <b-button              v-if="type === 'create'"              variant="deep-blue"              @click="create()"              block            >              Create            </b-button>            <b-button v-else variant="deep-blue" @click="update()" block>              Update            </b-button>          </b-col>          <b-col cols="2">            <b-button @click="hide" variant="outline-dark" block>              Close            </b-button>          </b-col>        </div>      </template>    </b-sidebar>  </div></template><script>import ContentHeader from '@/components/admininstration/ContentHeader';export default {  name: 'StakeholderEdit',  components: { ContentHeader },  props: {    type: {      type: String,      default() {        return '';      },    },    data: {      type: Number,      default: null,    },    bool: {      type: Boolean,      default: false,    },  },  data() {    return {      partnerType: {        name: null,        description: null,        is_system_default: false,      },    };  },  watch: {    bool(data) {      if (this.data) {        if (this.type === 'edit') {          const params = `${this.data}/`;          this.getPartnerType(params);        }      } else {        this.partnerType = {          name: null,          description: null,          is_system_default: false,        };      }    },  },  mounted() {},  methods: {    async getPartnerType(params) {      const response = await this.$store.dispatch('getPartnerTypes', params);      if (response.status === 200) {        this.partnerType = response.data;      }    },    async postPartnerType(params) {      const response = await this.$store.dispatch('postPartnerType', params);      if (response.status === 200) {        this.$root.$emit('bv::toggle::collapse', 'stakeholderSideBar');      }    },    create() {      const params = {        urlData: this.partnerType,      };      this.postPartnerType(params);    },    update() {      const params = {        urlParam: this.partnerType.id + '/',        urlData: {          id: this.partnerType.id,          updated_date: new Date(),          updated_user: this.$store.getters.user.id,          name: this.partnerType.name,          description: this.partnerType.description,          is_system_default: this.partnerType.is_system_default,        },      };      this.postPartnerType(params);    },  },};</script><style scoped>#astronaut {  position: absolute;  right: 20px;  top: -17px;}</style>