<template>  <Div>    <div class="container-fluid" style="background-color: #ffffff">      <b-col cols="12">        <b-col cols="12">          <CIPerfCard />        </b-col>      </b-col>      <b-col cols="12">        <div class="card pt-4 pl-4 pr-3 pb-3 bg-white">          <Table :tasks="employee_weeklyChecklist" class="pt-3" />        </div>      </b-col>    </div>  </Div></template><script>import CIPerfCard from '@/components/crucial_information/CIPerfCards';import Table from '@/components/crucial_information/tables/CI01Table';export default {  name: 'CrucialInformation01',  components: {    CIPerfCard,    Table,  },  layout: 'main',  data() {    return {      employee_weeklyChecklist: [],    };  },  head() {    return {      title: 'АЧ Мэдээлэл | ТУС Д+',    };  },  mounted() {    const params = '?page_size=52';    this.weeklyCrucialInfoData(params);  },  methods: {    async weeklyCrucialInfoData(params) {      const urlPath = this.$store.getters.activeCompany + '/' + params;      const response = await this.$store.dispatch(        'getWeeklyCrucialInfo',        urlPath      );      if (response.status === 200) {        this.employee_weeklyChecklist =          response.data.weekly_critical_info.results;      }    },  },};</script>