<template>  <div class="row-detail">    <table class="stage-detail">      <tbody>        <div v-for="(task, index) in data" :key="task.id" class="w-100">          <tr class="w-100">            <td style="width: 25px; height: 25px">              <i                class="bx bxs-chevron-down-circle task-detail-icon active"                @click="showTaskStageDetail(task.id, index)"              ></i>            </td>            <td style="min-width: 140px">              {{ task.code }}            </td>            <td class="w-100">              {{ task.name }}            </td>            <td style="min-width: 160px; width: 160px">              Due date: {{ task.end_date }}            </td>            <td style="width: 200px">              <b-progress                class="stage-progress"                :max="task.progress.max"                show-value              >                <b-progress-bar                  :value="task.progress.complete"                  variant="success"                ></b-progress-bar>                <b-progress-bar                  :value="task.progress.onProcess"                  variant="warning"                ></b-progress-bar>                <b-progress-bar                  :value="task.progress.onQueue"                  variant="danger"                ></b-progress-bar>              </b-progress>            </td>          </tr>          <tr :id="'task_' + task.id">            <td colspan="6">              <table class="stage-detail">                <tr v-for="(stage, indx) in task.stages" :key="indx">                  <td style="width: 120px">{{ stage.code }}</td>                  <td class="w-auto">{{ stage.name }}</td>                  <td style="width: 86px">                    <div class="d-inline">                      <b-input-group size="sm" style="width: 85px">                        <b-input-group-prepend>                          <b-button class="plan-time">                            <i                              class="bx bxs-time-five align-middle"                              variant="dark"                            ></i>                          </b-button>                        </b-input-group-prepend>                        <b-form-input                          class="plan-time"                          size="sm"                          disabled                          :value="stage.plan_time"                          type="text"                        ></b-form-input>                      </b-input-group>                    </div>                  </td>                  <td style="width: 65px">                    <b-badge v-if="stage.state === 'on_queue'" variant="danger"                      >On Queue                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'on_process'"                      variant="warning"                      >In Progress                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'complete'"                      variant="success"                      >Completed                    </b-badge>                  </td>                </tr>              </table>            </td>          </tr>        </div>      </tbody>    </table>  </div></template><script>export default {  name: 'NextWeekCIApproveTableDetail',  props: {    rowData: {      type: [Array, Object],      required: true,    },  },  data() {    return {      data: this.rowData,    };  },  mounted() {},  methods: {    showTaskStageDetail(id, index) {      if (document.getElementById('task_' + id).style.display === 'none') {        document.getElementById('task_' + id).style.display = '';      } else {        document.getElementById('task_' + id).style.display = 'none';      }      this.data[index].showStages = !this.data[index].showStages;      event.target.classList.toggle('active');    },  },};</script><style scoped>.stage-progress {  width: 200px;}.hidden-task-stageGroup {  display: none;}.plan-time {  border: 0;  background-color: #f8f8fb;  color: #495057;  font-size: 14px;  line-height: 16px;}.plan-time.warning {  border: 1px solid red !important;}.plan-time.success {  border: 1px solid green !important;}.newTab {  background-color: #2a3042;  min-width: 153px;  width: 153px;  color: white;}</style>