<template>  <div class="row-detail">    <table class="stage-detail">      <tbody>        <div          v-for="stageGRP in options"          :key="stageGRP.task_stage_group_id"          class="w-100"        >          <tr>            <td>              {{ stageGRP.stage_group_code }}            </td>            <td class="w-100">              {{ stageGRP.stage_group_name }}            </td>            <td style="width: 40px">              {{ stageGRP.plan_time }}            </td>            <td style="width: 100px">              {{ stageGRP.end_date }}            </td>            <td style="width: 130px">              <router-link                :to="                  'https://form.tuss.io/edit?id=' +                  stageGRP.form +                  '&did=' +                  stageGRP.entry +                  '&tsk=' +                  rowData.task_id +                  '&tsg=' +                  stageGRP.task_stage_group_id                "                target="_blank"              >                <button type="button" class="btn button">Perform Task</button>              </router-link>            </td>          </tr>          <tr>            <td colspan="5">              <table class="stage-detail" style="background-color: transparent">                <tr v-for="stage in stageGRP.task_stages" :key="stage.id">                  <td>{{ stage.stage_code }}</td>                  <td class="w-100">{{ stage.stage_name }}</td>                  <td>{{ stage.plan_time }}</td>                  <td class="d-flex justify-content-center">                    <ul class="user-list">                      <li v-for="(item, index) in stage.employees" :key="index">                        <img                          src="http://localhost:3000/_nuxt/assets/images/users/avatar-1.jpg"                          class="border-white rounded-circle con-vs-avatar -m-1"                          :alt="item.name"                        />                      </li>                    </ul>                  </td>                  <td>                    <b-badge v-if="stage.state === 'on_queue'" variant="danger"                      >Дуусаагүй                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'on_process'"                      variant="warning"                      >Хийж байгаа                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'complete'"                      variant="success"                      >Дууссан                    </b-badge>                  </td>                </tr>              </table>            </td>          </tr>        </div>      </tbody>    </table>  </div></template><script>export default {  name: 'TableDetail',  props: {    rowData: {      type: Object,      required: true,    },    options: {      type: Array,      default: () => [],    },  },  mounted() {},  methods: {},};</script>