<template>  <div>    <b-table striped hover :fields="tableFields" :items="tableDatas">      <template #cell(sequence)="props">        {{ props.index + 1 }}      </template>      <template #cell(name)="props">        <router-link          :to="{            name: 'task-detail',            query: {              id: props.item.id,            },          }"          class="task-link"        >          {{ props.item.name }}        </router-link>      </template>      <template #cell(created_date)="props">        <div style="width: 75px">{{ props.item.created_date | date }}</div>      </template>      <template #cell(executing_employee)="props">        <div class="mx-auto" style="width: 25px">          <img            :src="props.item.executing_employee.id | get_employee_pict"            onError="this.onerror=null;this.src='https://tuss.io/tusd_images/profile/human_icon.png';"            class="border-white rounded-circle con-vs-avatar -m-1"            width="25px"            :id="`img_${props.item.executing_employee.id}_${props.item.id}`"            v-b-tooltip.hover          />          <b-tooltip            :target="`img_${props.item.executing_employee.id}_${props.item.id}`"            >{{              props.item.executing_employee.id | get_employee_info            }}</b-tooltip          >        </div>      </template>      <template #row-details="props">        <TableDetail :row-data="props.item" :options="props.item.stgGrp" />      </template>    </b-table>  </div></template><script>import TableDetail from '@/components/crucial_information/tables/dashboardTableDetail';export default {  name: 'DashboardPlanTable',  components: { TableDetail },  props: {    tableData: {      type: [Array, Object],      default() {        return [];      },    },  },  data() {    return {      totalRows: 1,      tableFields: [        {          key: 'sequence',          label: '',        },        {          key: 'code',          label: 'Task code',          sortable: true,        },        {          key: 'name',          label: 'Task name',          sortable: true,        },        {          key: 'created_date',          label: 'Created date',        },        {          key: 'executing_employee',          label: 'Assignee employee',          sortable: true,        },      ],      tableDatas: [],    };  },  watch: {    tableData(data) {      this.tableDatas = data;    },  },  methods: {    linkGen(pageNum) {      return pageNum === 1 ? '?' : `?page=${pageNum}`;    },  },};</script><style scoped></style>