<template>  <div class="row-detail">    <table class="stage-detail">      <tbody>        <div v-for="stageGRP in options" :key="stageGRP.id" class="w-100">          <tr class="w-100">            <td>              {{ stageGRP.code }}            </td>            <td class="w-100">              {{ stageGRP.name }}            </td>            <td style="width: 100px">              {{ stageGRP.end_date }}            </td>            <td>              <div style="width: 114px">                <b-progress                  class="stage-progress"                  :max="stageGRP.progress_max"                  show-value                >                  <b-progress-bar                    :value="stageGRP.progress_complete"                    variant="success"                  ></b-progress-bar>                  <b-progress-bar                    :value="stageGRP.progress_onProcess"                    variant="warning"                  ></b-progress-bar>                  <b-progress-bar                    :value="stageGRP.progress_onQueue"                    variant="danger"                  ></b-progress-bar>                </b-progress>              </div>            </td>          </tr>          <tr>            <td colspan="4">              <table class="stage-detail" style="background-color: transparent">                <tr v-for="stage in stageGRP.stages" :key="stage.id">                  <td>{{ stage.code }}</td>                  <td class="w-100">{{ stage.name }}</td>                  <td>{{ stage.plan_hour }}</td>                  <td>                    <b-badge v-if="stage.state === 'on_queue'" variant="danger"                      >On queue                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'on_process'"                      variant="warning"                      >On process                    </b-badge>                    <b-badge                      v-else-if="stage.state === 'complete'"                      variant="success"                      >Complete                    </b-badge>                  </td>                </tr>              </table>            </td>          </tr>        </div>      </tbody>    </table>  </div></template><script>export default {  name: 'nextWeekCITaskViewTableDetail',  props: {    rowData: {      type: Object,      required: true,    },    options: {      type: Array,      default: () => [],    },  },  mounted() {},  methods: {},};</script><style scoped>.row-detail .stage-progress {  width: 114px;  max-width: 114px;}</style>