<template>  <div id="diagnose-header-dashboard">    <div>      <h5>Dashboard</h5>    </div>    <div>      <h5 class="text-center">{{ diagnoseTotal }}</h5>      <p class="text-center">Total diagnoses</p>    </div>    <div>      <h5 class="text-center">{{ diagnoseDraft }}</h5>      <p class="text-center">Draft</p>    </div>    <div>      <h5 class="text-center">{{ diagnoseThisMonth }}</h5>      <p class="text-center">This Month</p>    </div>  </div></template><script>export default {  name: 'DiagnoseHeaderDashboard',  data() {    return {      diagnoseTotal: 0,      diagnoseDraft: 0,      diagnoseThisMonth: 0,    };  },  mounted() {    let organization = this.$route.query.organization;    if (!organization) {      organization = this.$store.getters.activeCompany;    }    const params = organization + '/';    this.getDiagnoseDashboard(params);  },  methods: {    async getDiagnoseDashboard(params) {      const response = await this.$store.dispatch(        'diagnoseListDashboard',        params      );      if (response.status === 200) {        this.diagnoseTotal = response.data.all;        this.diagnoseDraft = response.data.draft;        this.diagnoseThisMonth = response.data.this_month;      }    },  },};</script>