<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-checkbox-group v-model="checkList">      <el-checkbox        v-for="item in currentField.options"        :key="item.optionValue"        :label="item.optionLabel"        :disabled="currentField.disabled"        @change="changeCheckBox"      >      </el-checkbox>    </el-checkbox-group>  </el-form-item></template><script>import fetchData from '@/plugins/api/fetch-data';export default {  name: 'Checkbox',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {      checkList: [],    };  },  mounted() {    if (this.currentField.isFromUrl) {      const dataUrl = this.currentField.dataUrl;      const valueField = this.currentField.valueField;      const labelField = this.currentField.labelField;      const promise = fetchData.fetchOptionsData(        dataUrl,        labelField,        valueField      );      promise.then((data) => {        this.currentField.options = data;      });    }  },  methods: {    changeCheckBox() {      this.currentField.entry = this.checkList;    },  },};</script><style>.el-checkbox-group {  font-size: 14px;}</style>