<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-date-picker      v-model="currentField.entry"      :disabled="currentField.disabled"      type="date"      placeholder="Pick a day"      @change="currentField.entry = $event"    >      >    </el-date-picker>  </el-form-item></template><script>export default {  name: 'DatePicker',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {      date: '',    };  },};</script>