<template>  <div    :disabled="currentField.disabled"    v-html="span(currentField.htmlContent)"    @change="currentField.entry = $event"  ></div></template><script>export default {  name: 'HtmlComponent',  props: {    currentField: {      type: Object,      default: null,    },    state: {      type: Object,      default: null,    },  },  data() {    return {};  },  methods: {    span(text) {      return `${text}`;    },  },};</script>