<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-input      v-model="currentField.entry"      type="textarea"      :rows="2"      :placeholder="        currentField.isPlaceholderVisible ? currentField.placeholder : ''      "      :disabled="currentField.disabled"      @change="currentField.entry = $event"    >    </el-input>  </el-form-item></template><script>export default {  name: 'LongTextInput',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {};  },};</script>