<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-input-number      v-model="currentField.entry"      controls-position="right"      v-bind="attributesBinding"      @change="currentField.entry = $event"    >    </el-input-number>  </el-form-item></template><script>export default {  name: 'NumberInput',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {      number: 0,    };  },  computed: {    attributesBinding() {      const attr = {};      attr.disabled = this.currentField.disabled;      attr.step = this.currentField.step;      attr['step-strictly'] = this.currentField.stepStrictly;      if (this.currentField.hasMinValue) {        attr.min = this.currentField.min;      }      if (this.currentField.hasMaxValue) {        attr.max = this.currentField.max;      }      return attr;    },  },};</script>