<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-radio      v-for="item in currentField.options"      :key="item.optionValue"      v-model="currentField.entry"      :label="item.optionValue"      :disabled="currentField.disabled"      @change="currentField.entry = $event"    >    </el-radio>  </el-form-item></template><script>import fetchData from '@/plugins/api/fetch-data';export default {  name: 'RadioButton',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {      radio: '1',    };  },  mounted() {    if (this.currentField.isFromUrl) {      const dataUrl = this.currentField.dataUrl;      const valueField = this.currentField.valueField;      const labelField = this.currentField.labelField;      const promise = fetchData.fetchOptionsData(        dataUrl,        labelField,        valueField      );      promise.then((data) => {        this.currentField.options = data;      });    }  },};</script>