<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-rate      v-model="currentField.entry"      :colors="currentField.colors"      :texts="currentField.texts"      :show-text="currentField.showText"      :disabled="currentField.disabled"      :show-score="currentField.showScore"      :score-template="'{value} ' + currentField.scoreUnit"      allow-half      @change="currentField.entry = $event"    >    </el-rate>  </el-form-item></template><script>export default {  name: 'Rating',  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {      value: null,    };  },};</script><style>.el-rate {  padding-top: 12px;}</style>