<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <el-switch      v-model="currentField.entry"      :active-text="currentField.activeText"      :inactive-text="currentField.inActiveText"      :disabled="currentField.disabled"      @change="change_switch($event)"    >    </el-switch>  </el-form-item></template><script>export default {  name: 'Elswitch',  props: {    forms: {      type: Array,      default() {        return [];      },    },    currentField: {      type: Object,      default: null,    },    formObj: {      type: Array,      default() {        return [];      },    },  },  data() {    return {      value: false,    };  },  methods: {    change_switch(event) {      this.currentField.entry = event;      if (event) {        for (let i = 0; i < this.forms[0].fields.length; i++) {          if (            this.forms[0].fields[i].isRequired &&            this.forms[0].fields[i].fieldType !== 'Button' &&            this.forms[0].fields[i].entry === ''          ) {            this.$message.warning(              'Та "' +                this.forms[0].fields[i].label +                '" заавал утга оруулна уу!'            );            this.currentField.entry = false;            break;          }        }      }    },  },};</script>