<template>  <el-form-item    :label="currentField.label"    :label-width="currentField.labelWidth + 'px'"  >    <editor      v-model="currentField.entry"      :api-key="currentField.api_key"      :disabled="currentField.disabled"      :placeholder="        currentField.isPlaceholderVisible ? currentField.placeholder : ''      "      :init="{        height: currentField.height,        menubar: false,        plugins: [          'advlist autolink lists link image charmap print preview anchor',          'searchreplace visualblocks code fullscreen',          'insertdatetime media table paste code help wordcount',        ],        toolbar:          'undo redo | formatselect | bold italic backcolor | \                        alignleft aligncenter alignright alignjustify | \                        bullist numlist outdent indent | removeformat | help',      }"      @change="currentField.entry = $event"    />  </el-form-item></template><script>import Editor from '@tinymce/tinymce-vue';export default {  name: 'LongTextInput',  components: { Editor },  props: {    currentField: {      type: Object,      default: null,    },  },  data() {    return {};  },};</script>