<template>  <div class="el-tabs__inner">    <el-form      ref="fieldProperties"      :model="fieldProperties"      :rules="rules"      :label-position="labelPosition"    >      <el-row>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('label')"            label="Нэр"          >            <el-input v-model="activeField.label">{{              activeField.label            }}</el-input>          </el-form-item>        </el-col>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('label')"            label="Нэрний өргөн"          >            <el-input-number              v-model="activeField.labelWidth"              :min="30"              :max="1000"              controls-position="right"            ></el-input-number>          </el-form-item>        </el-col>      </el-row>      <el-form-item        v-show="          activeField.hasOwnProperty('fieldType') &&          activeField['fieldType'] == 'Carousel'        "        label="Өндөр - px"      >        <el-input-number          v-model="activeField.controlHeight"          controls-position="right"        ></el-input-number>      </el-form-item>      <el-form-item        v-show="          activeField.hasOwnProperty('isPlaceholderVisible') &&          activeField['isPlaceholderVisible'] == true        "        label="Бүдэг текст"      >        <el-row>          <el-col :span="5">            <el-switch v-model="activeField.isPlaceholderVisible"></el-switch>          </el-col>          <el-col :span="19">            <el-input              v-show="activeField.isPlaceholderVisible"              v-model="activeField.placeholder"            >              {{ activeField.placeholder }}            </el-input>          </el-col>        </el-row>      </el-form-item>      <el-row>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('isRequired')"            label="Заавал эсэх"          >            <el-switch v-model="activeField.isRequired"></el-switch>          </el-form-item>        </el-col>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('span')"            label="Дээд утга 24"          >            <el-input-number              v-model="activeField.span"              :min="1"              :max="24"              controls-position="right"            ></el-input-number>          </el-form-item>        </el-col>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('height')"            label="Өндөр"          >            <el-input-number              v-model="activeField.height"              :min="100"              :max="1000"              controls-position="right"              @change="activeField.height = $event"            ></el-input-number>          </el-form-item>        </el-col>      </el-row>      <el-form-item        v-show="activeField.hasOwnProperty('buttonText')"        label="Товчлуурын текст"      >        <el-input v-model="activeField.buttonText">          {{ activeField.buttonText }}        </el-input>      </el-form-item>      <el-form-item        v-show="activeField.hasOwnProperty('fieldText')"        label="Код харах"      >        <el-input v-model="activeField.fieldText" type="textarea" :rows="10">          {{ activeField.fieldText }}        </el-input>      </el-form-item>      <el-row>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('activeText')"            label="Active Text"          >            <el-input v-model="activeField.activeText">{{              activeField.activeText            }}</el-input>          </el-form-item>        </el-col>        <el-col :span="12">          <el-form-item            v-show="activeField.hasOwnProperty('inActiveText')"            label="Inactive Text"          >            <el-input v-model="activeField.inActiveText">{{              activeField.inActiveText            }}</el-input>          </el-form-item>        </el-col>      </el-row>      <el-form-item        v-show="activeField.hasOwnProperty('uploadURL')"        label="uploadURL"      >        <el-input v-model="activeField.uploadURL">{{          activeField.uploadURL        }}</el-input>      </el-form-item>      <el-form-item v-show="activeField.hasOwnProperty('items')" label="Items">        <li          v-for="(item, index) in activeField.items"          :key="index"          class="properties__optionslist"        >          <el-row :gutter="5">            <el-col :span="20">              <el-input v-model="item.url">{{ item.url }}</el-input>            </el-col>            <el-col :span="4">              <el-button                v-show="activeField.items.length > 1"                @click="deleteOption(activeField.items, index)"              >                >                <i class="el-icon-error"></i>              </el-button>            </el-col>          </el-row>        </li>        <el-button type="text" @click="addItem(activeField.items)">          <i class="el-icon-plus"></i>          Add more        </el-button>      </el-form-item>      <el-form-item        v-if="!activeField.isFromUrl && activeField.options"        label="Сонголт"      >        <ul class="properties__optionsul">          <li class="properties__optionslist">            <el-row :gutter="5">              <el-col :span="10"> Нэр </el-col>              <el-col :span="10"> Утга </el-col>              <el-col :span="4"> </el-col>            </el-row>          </li>          <li            v-for="(item, index) in activeField.options"            :key="index"            class="properties__optionslist"          >            <el-row :gutter="5">              <el-col :span="10">                <el-input v-model="item.optionLabel">{{                  item.optionLabel                }}</el-input>              </el-col>              <el-col :span="10">                <el-input v-model="item.optionValue">{{                  item.optionValue                }}</el-input>              </el-col>              <el-col :span="4">                <el-button                  v-show="activeField.options.length > 1"                  @click="deleteOption(activeField.options, index)"                >                  >                  <i class="el-icon-error"></i>                </el-button>              </el-col>            </el-row>          </li>        </ul>        <el-button type="text" @click="addOption(activeField.options)">          <i class="el-icon-plus"></i>          Нэмэх        </el-button>      </el-form-item>      <el-form-item        v-show="activeField.fieldType === 'TableComponent'"        label="Table Columns"      >        <ul class="properties__optionsul">          <li class="properties__optionslist">            <el-row :gutter="5">              <el-col :span="7"> Prop </el-col>              <el-col :span="7"> Label </el-col>              <el-col :span="7"> Width </el-col>              <el-col :span="3"> </el-col>            </el-row>          </li>          <li            v-for="(column, index) in activeField.tableColumns"            :key="index"            class="properties__optionslist"          >            <el-row :gutter="5">              <el-col :span="7">                <el-input v-model="column.prop">{{ column.prop }}</el-input>              </el-col>              <el-col :span="7">                <el-input v-model="column.label">{{ column.label }}</el-input>              </el-col>              <el-col :span="7">                <el-input v-model="column.width" @change="columnWidth">{{                  column.width                }}</el-input>              </el-col>              <el-col :span="3">                <el-button                  v-show="activeField.tableColumns.length > 1"                  @click="                    deleteColumn(activeField.tableColumns, index, column.prop)                  "                >                  <i class="el-icon-error"></i>                </el-button>              </el-col>            </el-row>          </li>        </ul>        <el-button type="text" @click="addColumn(activeField.tableColumns)">          <i class="el-icon-plus"></i>          Add more        </el-button>      </el-form-item>      <el-form-item        v-show="activeField.hasOwnProperty('htmlContent')"        label="Html Content"      >        <el-input v-model="activeField.htmlContent" :rows="10" type="textarea"          >{{ activeField.htmlContent }}        </el-input>      </el-form-item>      <el-button        v-show="activeField.hasOwnProperty('advancedOptions')"        style="width: 100%"        type="success"        @click="advancedPropsVisible = true"      >        Нэмэлт тохиргоо      </el-button>      <el-dialog        :close-on-click-modal="false"        title="Нэмэлт тохиргоо"        :visible.sync="advancedPropsVisible"      >        <el-form ref="OptionsForm" :rules="dialogRules">          <rating-advanced-props            v-if="activeField.fieldType === 'Rating'"          ></rating-advanced-props>          <text-input-advanced-props            v-if="activeField.fieldType === 'TextInput'"          ></text-input-advanced-props>          <html-advanced-props            v-if="activeField.fieldType === 'HtmlComponent'"          ></html-advanced-props>          <number-input-advanced-props            v-if="activeField.fieldType === 'NumberInput'"          ></number-input-advanced-props>          <select-list-advanced-props            v-if="activeField.fieldType === 'SelectList'"          ></select-list-advanced-props>          <options-advanced-props            v-if="              activeField.fieldType === 'RadioButton' ||              activeField.fieldType === 'Checkbox'            "          ></options-advanced-props>        </el-form>        <div slot="footer" class="dialog-footer">          <el-button type="success" @click="confirmForm">Хадгалах</el-button>        </div>      </el-dialog>    </el-form>  </div></template><script>import RatingAdvancedProps from './RatingAdvancedProps';import TextInputAdvancedProps from './TextInputAdvancedProps.vue';import HtmlAdvancedProps from './HtmlAdvancedProps.vue';import NumberInputAdvancedProps from './NumberInputAdvancedProps.vue';import OptionsAdvancedProps from './OptionsAdvancedProps.vue';import SelectListAdvancedProps from './SelectListAdvancedProps.vue';export default {  name: 'Properties',  components: {    RatingAdvancedProps,    TextInputAdvancedProps,    HtmlAdvancedProps,    NumberInputAdvancedProps,    OptionsAdvancedProps,    SelectListAdvancedProps,  },  props: {    activeField: {      type: [Array, Object],      default: null,    },  },  data() {    return {      labelPosition: 'top',      fieldProperties: {},      rules: {},      advancedPropsVisible: false,      dialogRules: {        dataUrl: [          { required: true, message: 'Please input url', trigger: 'change' },        ],      },    };  },  methods: {    openModal() {      this.$refs.modal.showModal();    },    deleteOption(option, index) {      this.$delete(option, index);    },    addOption(option) {      const count = option.length + 1;      option.push({        optionLabel: 'Option Label ' + count,        optionValue: 'Option ' + count,      });    },    addItem(item) {      item.push({        url: '',      });    },    deleteColumn(column, index, prop) {      this.$delete(column, index);      this.activeField.tableDatas.forEach(function (ele) {        delete ele[prop];      });    },    addColumn(tableColumns) {      tableColumns.push({        prop: '',        label: '',        width: 180,      });    },    columnWidth(value) {      if (value && isNaN(value)) {        this.$message.error('Column width should be a number!');      }    },    confirmForm() {      this.$refs.OptionsForm.validate((valid) => {        if (valid) {          this.$message.success('Амжилттай хадгалагдлаа!');        } else {          console.log('error submit!!');          return false;        }      });    },  },};</script><style lang="scss" scoped>.properties__optionslist {  margin-bottom: 5px;  list-style: none;  list-style-type: none;}.properties__optionsul {  padding: 0;}</style>