<template>  <div>    <el-row>      <el-col :span="24">        <el-form-item label="Colors">          <el-row :gutter="3">            <el-col              v-for="(color, index) in activeField.colors"              :key="index"              :span="7"            >              <el-input v-model="activeField.colors[index]"></el-input>            </el-col>          </el-row>        </el-form-item>      </el-col>    </el-row>    <el-row>      <el-col :span="12">        <el-form-item label="Show Text">          <el-switch            v-model="activeField.showText"            @change="showTextChange"          ></el-switch>        </el-form-item>      </el-col>      <el-col :span="12">        <el-form-item label="Disabled">          <el-switch v-model="activeField.disabled"></el-switch>        </el-form-item>      </el-col>    </el-row>    <el-row>      <el-col :span="24">        <el-form-item v-show="activeField.showText" label="Texts">          <el-row :gutter="3">            <el-col              v-for="(text, index) in activeField.texts"              :key="index"              :span="4"            >              <el-input v-model="activeField.texts[index]"></el-input>            </el-col>          </el-row>        </el-form-item>      </el-col>    </el-row>    <el-row>      <el-col :span="12">        <el-form-item label="Show Score">          <el-switch            v-model="activeField.showScore"            @change="showScoreChange"          ></el-switch>        </el-form-item>      </el-col>      <el-col :span="12">        <el-form-item v-show="activeField.showScore" label="Score Unit">          <el-input v-model="activeField.scoreUnit"></el-input>        </el-form-item>      </el-col>    </el-row>  </div></template><script>export default {  name: 'RatingAdvancedProp',  data() {    return {      name: '',      activeField: this.$store.state.activeField,    };  },  methods: {    showTextChange(value) {      if (value && this.activeField.showScore) {        this.activeField.showScore = false;      }    },    showScoreChange(value) {      if (value && this.activeField.showText) {        this.activeField.showText = false;      }    },  },};</script><style lang="scss" scoped>.properties__optionslist {  margin-bottom: 5px;  list-style: none;  list-style-type: none;}.properties__optionsul {  padding: 0px;}</style>