<template>  <div>    <el-row>      <el-col :span="8">        <el-form-item label="Password">          <el-switch            v-model="activeField.showPassword"            @change="passwordChange"          ></el-switch>        </el-form-item>      </el-col>      <el-col :span="8">        <el-form-item label="Disabled">          <el-switch v-model="activeField.disabled"></el-switch>        </el-form-item>      </el-col>      <el-col :span="8">        <el-form-item label="Clearable">          <el-switch v-model="activeField.clearable"></el-switch>        </el-form-item>      </el-col>    </el-row>    <el-row>      <el-col :span="12">        <el-form-item label="Prepend">          <el-input v-model="activeField.prepend"></el-input>        </el-form-item>      </el-col>      <el-col :span="12">        <el-form-item label="Append">          <el-input v-model="activeField.append"></el-input>        </el-form-item>      </el-col>    </el-row>    <el-row>      <el-col :span="12">        <el-form-item label="Show Word Limit">          <el-switch            v-model="activeField.showWordLimit"            @change="showWordLimitChange"          ></el-switch>        </el-form-item>      </el-col>      <el-col :span="12">        <el-form-item v-show="activeField.showWordLimit" label="Word Limit">          <el-input-number            v-model="activeField.maxlength"            :min="1"          ></el-input-number>        </el-form-item>      </el-col>    </el-row>  </div></template><script>export default {  name: 'RatingAdvancedProp',  data() {    return {      name: '',      activeField: this.$store.state.activeField,    };  },  methods: {    passwordChange(value) {      if (value) {        this.activeField.showWordLimit = false;      }    },    showWordLimitChange(value) {      if (value) {        this.activeField.showPassword = false;      }    },  },};</script>