<template>  <div>    <b-row id="search-body" class="w-100">      <b-col class="overflow-auto h-100" cols="12">        <b-row class="m-3">          <p id="header">            <i class="bx bx-menu align-middle mr-1" />THE COMPANY REPORTS          </p>        </b-row>        <b-row v-if="data" id="company-report">          <b-col class="w-100" cols="12">            <b-col class="mt-1 p-0 d-flex" cols="12">              <b-col cols="10" class="d-flex pl-0">                <p class="hover-menu">                  <a                    class="align-self-center col-md-11 textBold"                    :href="                      $router.resolve({                        name: 'shm-list',                        params: {                          type: 'organization',                          id: $store.getters.activeCompany,                        },                      }).href                    "                  >                    {{ data.organization }}</a                  >                </p>              </b-col>            </b-col>            <b-col cols="12">              <div>                <b-row                  v-for="partner_type in data.partner_types"                  :key="partner_type.partner_type_id"                >                  <b-col cols="12">                    <b-row class="d-flex mt-3 mb-1">                      <b-col cols="10" class="d-flex">                        <div class="align-self-center col-md-11">                          <a                            :href="                              $router.resolve({                                name: 'shm-list',                                params: {                                  type: 'partner_type',                                  id: partner_type.partner_type_id,                                },                              }).href                            "                            ><p class="hover-menu text">                              {{ partner_type.partner_type }}                            </p></a                          >                        </div>                      </b-col>                      <b-col cols="2" class="align-self-center">                        <i                          :id="partner_type.partner_type_id"                          class="                            arrowIcon                            color-orange                            bx                            bxs-caret-right-circle                            bx-sm                          "                          @click="arrowButton($event)"                        ></i>                      </b-col>                    </b-row>                    <div                      :id="'partner_type_' + partner_type.partner_type_id"                      style="display: none"                    >                      <div class="d-flex mt-2">                        <p class="ml-5 text-muted">1 of 320</p>                        <Fa                          class="ml-auto mr-3"                          size="lg"                          :icon="['fas', 'caret-left']"                        />                        <Fa size="lg" :icon="['fas', 'caret-right']" />                      </div>                      <b-row                        v-for="member in partner_type.partners"                        :id="'employee_' + member.partner__id"                        :key="member.partner__id"                        class="ml-3"                      >                        <b-col cols="12" class="mt-2 mb-2 flex-space-between">                          <b-col cols="1">                            <img                              :src="member.image"                              onError="this.onerror=null;this.src='https://tuss.io/tusd_images/profile/human_icon.png';"                              class="avatar-xs"                              alt                            />                          </b-col>                          <b-col cols="11" class="align-self-center">                            <a                              :href="                                $router.resolve({                                  name: 'stake-holder-detail',                                  params: {                                    id: member.partner_id,                                  },                                }).href                              "                              ><p class="hover-menu text">                                {{ member.name }}                              </p></a                            >                          </b-col>                        </b-col>                      </b-row>                    </div>                  </b-col>                </b-row>              </div>            </b-col>          </b-col>        </b-row>      </b-col>    </b-row>    <div      class="        bg-secondary        d-flex        justify-content-center        p-2        cursor-pointer        w-100      "      style="bottom: 0; position: absolute"    >      <Fa class="align-self-center mr-2" :icon="['fas', 'cog']" />      <a        :href="          $router.resolve({            name: 'stake-holders-types',          }).href        "        ><p>Setting</p>      </a>    </div>  </div></template><script>export default {  name: 'Ach',  data() {    return {      name: null,      data: null,    };  },  mounted() {    if (this.data === null) {      const type = 'organization';      const params = `?${type}=${this.$store.getters.activeCompany}`;      this.getPartnerTypeRel(params);    }    this.name = this.$store.getters.user.partner_id;  },  methods: {    async getPartnerTypeRel(params) {      const response = await this.$store.dispatch('getPartnerTypeRel', params);      if (response.status === 200) {        this.data = response.data;      }    },    arrowButton(event) {      const id = document.getElementById(event.target.id);      id.classList.toggle('bxs-caret-down-circle');      id.classList.toggle('bxs-caret-right-circle');      const depId = document.getElementById('partner_type_' + event.target.id);      if (depId.style.display === 'block') {        depId.style.display = 'none';      } else {        depId.style.display = 'block';      }    },  },};</script>