<template>  <b-col cols="12">    <b-col cols="12" class="d-flex mt-5 mb-3">      <b-input-group class="search-form">        <template #append>          <b-input-group-text class="bg-secondary text-dark">            <Fa :icon="['fas', 'search']" />          </b-input-group-text>        </template>        <b-form-input          v-model="searchData"          class="bg-secondary text-dark"          id="search-task"          placeholder="Search"        ></b-form-input>      </b-input-group>      <div class="ml-auto d-flex align-items-center">        <div class="mr-4 bg-secondary">          <div            class="btn"            @click="setMenu('grid')"            :class="{ active: menuType === 'grid' }"          >            <Fa              :icon="['far', 'th-large']"              :class="{ 'text-primary': menuType === 'grid' }"            />          </div>          <div            class="btn"            @click="setMenu('list')"            :class="{ active: menuType === 'list' }"          >            <Fa              :icon="['fas', 'list-ul']"              :class="{ 'text-primary': menuType === 'list' }"            />          </div>        </div>        <b-button @click="create" variant="deep-blue">          <Fa :icon="['fas', 'plus']" />          Add Organization        </b-button>      </div>    </b-col>    <b-col cols="12" v-if="menuType === 'list'">      <Table :table-data="tableData.results" class="pt-3" @emit_id="edit" />    </b-col>    <b-col cols="12" v-if="menuType === 'grid'">      <div class="d-flex" style="flex-wrap: wrap">        <div          class="mx-3"          v-for="(item, index) in tableData.results"          :key="index"        >          <Card :card-data="item" />        </div>      </div>    </b-col>    <b-col class="mb-4" cols="12">      <div class="d-flex justify-content-center mt-4">        <b-pagination-nav          pills          :link-gen="linkGen"          :number-of-pages="tableData.total <= 50 ? 1 : tableData.total / 50"          use-router          first-number          last-number        ></b-pagination-nav>      </div>      <p class="text-right">Нийт: {{ tableData.total }}</p>    </b-col>    <SideBar      :type="sideBarType"      :data="sideBarData"      :group-id="$route.params.groupId"      :bool="bool"    />  </b-col></template><script>import Card from '@/components/settings/system-admin/items/OrgCard';import Table from '@/components/settings/system-admin/tables/OrganizationTable';import SideBar from '@/components/settings/system-admin/items/sideBarOrgCreateEdit';export default {  name: 'OrganizationsList',  components: { Card, Table, SideBar },  data() {    return {      searchData: '',      tableData: [],      tableField: [],      menuType: 'list',      sequence: {        key: 'sequence',        label: '',      },      sideBarType: null,      sideBarData: null,      bool: false,    };  },  watch: {    pageNum(page) {      const params = `?group=${this.$route.params.groupId}&page=${page}`;      this.getPartners(params);    },  },  mounted() {    const params = `?group=${this.$route.params.groupId}`;    this.getOrganizations(params);  },  methods: {    async getOrganizations(params) {      const response = await this.$store.dispatch('getOrganizations', params);      if (response.status === 200) {        this.tableData = response.data;      }    },    edit(data) {      this.sideBarType = 'Edit';      this.sideBarData = data;      this.bool = !this.bool;      this.$root.$emit('bv::toggle::collapse', 'sideBarOrgCreateEdit');    },    create() {      this.sideBarType = 'Create';      this.bool = !this.bool;      this.$root.$emit('bv::toggle::collapse', 'sideBarOrgCreateEdit');    },    setMenu(type) {      this.menuType = type;    },    linkGen(pageNum) {      this.pageNum = pageNum;      return pageNum === 1 ? '?' : `?page=${pageNum}`;    },  },};</script><style scoped>.active {  border: #4276d0 2px solid !important;}</style>