<template>  <div v-if="show" id="loader-container">    <div id="loader"></div>  </div></template><script>export default {  name: 'Spinner',  data() {    return {      show: false,      active: this.$store.getters.spinner,    };  },  created() {    this.unwatch = this.$store.watch(      (state, getters) => getters.spinner,      (newValue) => {        this.show = newValue === true;      }    );  },  beforeDestroy() {    this.unwatch();  },};</script>