<template>  <div class="row-detail">    <table class="stage-detail">      <tr v-for="stage_group in stage_groups" :key="stage_group.id">        <td>          {{            stage_group.stage_group              | stageGroupName($store.getters.company.algorithm)          }}        </td>        <td>          <b-progress            class="stage-progress"            :value="stage_group.cmp_stage_count"            :max="stage_group.all_stage_count"            show-progress            variant="dark"          ></b-progress>        </td>        <td>          {{ stage_group.cmp_stage_count }}/{{ stage_group.all_stage_count }}        </td>        <td>{{ stage_group.employees }}</td>        <td>{{ stage_group.start_date }}</td>        <td>{{ stage_group.end_date }}</td>        <td>          <b-badge v-if="stage_group.state === 'on_queue'" variant="danger"            >Дуусаагүй          </b-badge>          <b-badge v-if="stage_group.state === 'on_process'" variant="warning"            >Хийж байгаа          </b-badge>          <b-badge v-if="stage_group.state === 'complete'" variant="success"            >Дууссан          </b-badge>        </td>      </tr>    </table>  </div></template><script>export default {  props: {    rowData: {      type: Object,      required: true,    },    rowIndex: {      type: Number,      default: 0,    },    options: {      type: Object,      default: null,    },  },  data() {    return {      stage_groups: [],    };  },  created() {    const context = {      task: this.rowData.id,      active: '1',    };    this.$store      .dispatch('taskStageGroup', context)      .then((response) => {        this.stage_groups = response.data.results;      })      .catch((error) => {        console.log(error);      });  },};</script>