<template>  <div class="card mini-stats-wid h-100">    <div class="card-body flex-space-between h-100 px-5">      <div class="stat">        <h4 class="text-center">{{ taskTotal }}</h4>        <p class="text-center mt-2">Total Configurations</p>      </div>      <div class="stat">        <h4 class="text-center">{{ taskConfirmed }}</h4>        <p class="text-center mt-2">Confirmed Configurations</p>      </div>      <div class="stat">        <h4 class="text-center">{{ taskPending }}</h4>        <p class="text-center mt-2">Pending for approval</p>      </div>    </div>  </div></template><script>import axios from 'axios';export default {  name: 'ConfigurationDashboard',  data() {    return {      taskTotal: 0,      taskConfirmed: 0,      taskPending: 0,    };  },  mounted() {    this.tableLoad();  },  methods: {    tableLoad() {      const activeCompany = this.$store.getters.activeCompany;      const url = `/pages/plan/task_coordination_dashboard/${activeCompany}/`;      axios.get(url).then((response) => {        this.taskTotal = response.data.total_task_coordinations;        this.taskConfirmed = response.data.confirmed_task_coordinations;        // const draft = response.data.draft_task_coordinations;        const sent = response.data.sent_task_coordinations;        this.taskPending = sent;      });    },  },};</script>