<template>  <div class="card mini-stats-wid h-100">    <div class="card-body flex-space-between h-100 px-5">      <div class="stat">        <h4>Summary</h4>        <b-form-select          v-model="taskYear"          size="sm"          class="mt-2"          :options="yearList"          @change="getTaskDashboard"        ></b-form-select>      </div>      <div class="stat">        <h4 class="text-center">{{ taskTotal }}</h4>        <p class="text-center mt-2">Total tasks</p>      </div>      <div class="stat vl" />      <div class="stat">        <h4 class="text-center">{{ taskConfirm }}</h4>        <p class="text-center mt-2">Need to confirm</p>      </div>      <div class="stat vl" />      <div class="stat">        <h4 class="text-center">{{ taskNew }}</h4>        <p class="text-center mt-2">New tasks</p>      </div>    </div>  </div></template><script>import axios from 'axios';export default {  name: 'TaskHeaderDashboard',  data() {    return {      taskTotal: null,      taskConfirm: null,      taskNew: null,      taskYear: null,      yearList: [        { value: 2020, text: '2020' },        { value: 2021, text: '2021' },        { value: 2022, text: '2022' },        { value: 2023, text: '2023' },        { value: 2024, text: '2024' },      ],    };  },  created() {    const year = this.$route.query.year;    if (year) {      this.taskYear = year;    } else {      this.taskYear = new Date().getFullYear();    }  },  mounted() {    this.getTaskDashboard(this.taskYear);  },  methods: {    getTaskDashboard() {      let organization = this.$route.query.organization;      if (!organization) {        organization = this.$store.getters.activeCompany;      }      const url = `/pages/plan/list_task_dashboard/${organization}/${this.taskYear}/`;      axios.get(url, { data: {} }).then((response) => {        this.taskTotal = response.data.total_tasks;        this.taskConfirm = response.data.draft_tasks;        this.taskNew = response.data.newly_tasks;      });    },  },};</script>