<template>  <b-form-group label="Algorithm">    <treeselect      v-model="algorithmSelected"      :options="options"      :multiple="false"      :clearable="false"      :disable-branch-nodes="true"      @input="change"    >      <label slot="option-label" slot-scope="{ node }">        {{ node.raw.label }}      </label>      <div slot="value-label" slot-scope="{ node }">        {{ node.raw.label }}      </div>    </treeselect>    <p      v-if="!$v.algorithmSelected.required && formSubmit"      class="text-danger mb-4"    >      Algorithm is required    </p>  </b-form-group></template><script>import Treeselect from '@riophae/vue-treeselect';import { required } from 'vuelidate/lib/validators';export default {  name: 'TaskAlgorithm',  components: {    Treeselect,  },  props: {    formSubmit: Boolean,    defaultValue: {      type: Number,      default() {        return null;      },    },  },  data() {    return {      algorithmSelected: null,      algorithm: null,    };  },  computed: {    options() {      const algorithmList = [];      for (const algorithm of this.$store.getters.company.algorithm) {        if (algorithm.is_active === '1') {          algorithmList.push({            id: algorithm.id,            label: algorithm.name,          });        }      }      return algorithmList;    },  },  watch: {    defaultValue() {      this.loadDefault();    },  },  validations: {    algorithmSelected: { required },  },  mounted() {    this.loadDefault();  },  methods: {    loadDefault() {      if (this.defaultValue) {        this.algorithmSelected = this.defaultValue;      }    },    change() {      this.$emit('getAlgorithm', this.algorithmSelected);    },  },};</script>