<template>  <b-form-group label="Task type">    <treeselect      v-model="selected"      :options="options"      :multiple="false"      :clearable="false"      @input="change"    />    <p v-if="!$v.selected.required && formSubmit" class="text-danger mb-4">      Task type is required    </p>  </b-form-group></template><script>import Treeselect from '@riophae/vue-treeselect';import { required } from 'vuelidate/lib/validators';export default {  name: 'TaskType',  components: {    Treeselect,  },  props: {    formSubmit: Boolean,    defaultValue: {      type: Number,      default() {        return null;      },    },  },  data() {    return {      selected: null,    };  },  computed: {    options() {      const optionList = [];      for (const taskType of this.$store.getters.company.task_type) {        if (taskType.active === '1') {          optionList.push({            id: taskType.id,            label: `${taskType.code} | ${taskType.name}`,          });        }      }      return optionList;    },  },  watch: {    defaultValue() {      this.loadDefault();    },  },  validations: {    selected: { required },  },  mounted() {    this.loadDefault();  },  methods: {    loadDefault() {      if (this.defaultValue) {        this.selected = this.defaultValue;      }    },    change() {      this.$emit('getType', this.selected);    },  },};</script>