<template>  <b-row>    <b-col cols="12"      ><div class="card flex-space-between mini-stats-wid">        <div class="card-body">          <div class="media">            <b-col class="media-body" cols="12">              <p class="text-left pt-2">                <router-link class="btn text-dark" to="/test"                  ><i class="bx bx-chevron-left"></i> Буцах                </router-link>              </p>              <p id="title" class="text-left pt-2">Ажил гүйцэтгэх алгоритм</p>              <p id="title-desc" class="text-left">A description of a board.</p>              <simplebar data-simplebar-auto-hide="true" class="h-100">                <b-row id="body" class="mx-1 mt-3">                  <b-col class="mr-2" cols="12">                    <swiper :options="swiperOption" class="d-flex mt-2">                      <swiper-slide                        v-for="item in stageGroup"                        id="tab-button"                        :key="item.id"                        class="btn flex-space-between"                        :class="{ active: item.isActive }"                      >                        <div                          class="btn btn-secondary float-left"                          @click="activate(item)"                        >                          <p>                            {{ item.name }}                          </p>                        </div>                        <div                          id="template"                          class="btn btn-secondary float-right"                        >                          <p>Mаягт</p>                        </div> </swiper-slide                      ><swiper-slide                        id="add-stage-group"                        class="btn flex-space-between mt-1"                        @click="addStage(item)"                      >                        <p class="algorithm-stage-title align-middle w-100">                          + Үе шат нэмэх                        </p>                      </swiper-slide>                    </swiper>                    <div>                      <b-row class="mt-4 ml-1">                        <b-col cols="3">                          <div                            v-for="key in bodyItem.data"                            :key="key.title"                            class="                              algorithm-stage                              flex-space-between                              py-2                              mx-2                              mb-3                              cursor-pointer                            "                            :class="{ active: key.isActive }"                            @click="stage(key)"                          >                            <b-col class="my-3" cols="12">                              <p class="algorithm-stage-title">                                {{ key.title }}                                <span class="float-right">                                  <i                                    class="bx bx-edit-alt color-green"                                    @click="editStage(key)"                                  ></i>                                  <i                                    class="bx bx-trash color-red"                                    @click="removeStage(key)"                                  ></i>                                </span>                              </p>                              <p id="algorithm-stage-body" class="mt-2">                                {{ key.data }}                              </p>                            </b-col>                          </div>                          <div                            id="algorithm-stage-add"                            class="py-2 d-flex mx-2 mb-3 cursor-pointer"                            @click="addStageGroup()"                          >                            <b-col class="my-3 align-items-center" cols="12">                              <p class="algorithm-stage-title align-middle">                                + Үе шат нэмэх                              </p>                            </b-col>                          </div>                        </b-col>                        <b-col class="mb-4" cols="9">                          <div class="algorithm-stage w-100 h-100">                            <FormViewer                              class="w-100 h-100"                              :form-template="stageTemplate"                            />                          </div>                        </b-col>                      </b-row>                    </div>                  </b-col>                </b-row>              </simplebar>            </b-col>          </div>        </div>      </div>    </b-col>  </b-row></template><script>import FormViewer from '@/components/testPage/Testitem4Component';import simplebar from 'simplebar-vue';import { Swiper, SwiperSlide } from 'vue-awesome-swiper';import 'swiper/swiper-bundle.css';export default {  name: 'AlgorithmBuilder',  components: { simplebar, FormViewer, Swiper, SwiperSlide },  data() {    return {      swiperOption: {        slidesPerView: 4,        spaceBetween: 20,        updateOnWindowResize: false,        grabCursor: true,        mousewheel: true,      },      stageGroup: [        {          name: 'Зорилго тодорхойлох',          id: '1',          isActive: true,          data: [            {              title: 'ЗТ-1',              isActive: false,              data: 'Нэгдмэл зорилгыг хэрэгжүүлэхэд тулгарч буй асуудлыг тодорхойлох',            },            {              title: 'ЗТ-2',              isActive: false,              data: 'Нэгдмэл зорилгыг хэрэгжүүлэхэд тулгарч буй асуудлыг тодорхойлох',            },          ],        },        {          name: 'Мэдээлэл цуглуулах',          id: '2',          isActive: false,          data: [            {              title: 'МЦ-1',              isActive: false,              data: 'Нэгдмэл зорилгыг хэрэгжүүлэхэд тулгарч буй асуудлыг тодорхойлох',            },            {              title: 'МЦ-2',              isActive: false,              data: 'Нэгдмэл зорилгыг хэрэгжүүлэхэд тулгарч буй асуудлыг тодорхойлох',            },          ],        },        {          name: 'Боловсруулах',          id: '3',          isActive: false,          data: [],        },        {          name: 'Ажил хэрэгжүүлэх',          id: '4',          isActive: false,          data: [],        },      ],      bodyItem: null,      stageTemplate: null,    };  },  created() {    this.bodyItem = this.stageGroup[0];  },  methods: {    activate(data) {      this.bodyItem = data;      for (const value in this.stageGroup) {        if (this.stageGroup[value].id === data.id) {          data.isActive = true;        } else {          this.stageGroup[value].isActive = false;        }      }    },    addStage() {      this.bodyItem.data.push("{title: '',data:'',}");    },    removeStage(data) {      console.log(this.bodyItem);      console.log(data);    },    editStage(data) {      console.log(data);    },    stage(data) {      if (data.isActive !== true) {        for (const value in this.bodyItem.data) {          this.bodyItem.data[value].isActive = false;        }      }      data.isActive = !data.isActive;      this.stageTemplate = [{ item1: 'aa', item2: 'bb' }];    },    addStageGroup(data) {      console.log(data);    },  },};</script><style scoped>#title {  font-family: Roboto;  font-style: normal;  font-weight: bold;  font-size: 32px;  line-height: 37px;  display: flex;  align-items: center;  color: #212121;}#title-desc {  font-family: Roboto;  font-style: normal;  font-weight: normal;  font-size: 14px;  line-height: 16px;  display: flex;  align-items: center;  letter-spacing: 0.1px;  color: #5a5a65;}#body {  overflow-x: auto;  background: #f8f8f8;  border-radius: 12px;}#tab-button {  background-color: transparent;  color: #2a3042;  border: 0;  cursor: context-menu;}#add-stage-group {  background-color: transparent;  color: #2a3042;  border: 2px dashed orange;  cursor: pointer;}#tab-button .btn-secondary {  background-color: #e1e4e8;  color: #2a3042;  border: 0;}#tab-button .btn-secondary.active {  background-color: #f8f8f8;  color: #2a3042;}#tab-button #template.btn-secondary {  background-color: #cbdfd8;}#tab-button #template.btn-secondary.active {  background-color: #b6c8c2;}.algorithm-stage {  background: #ffffff;  /* border: 1px solid #dddddd; */  box-sizing: border-box;  box-shadow: 0px 4px 5px rgba(0, 0, 0, 0.08);  border-radius: 8px;}.algorithm-stage.active {  border: 1px dashed orange;  box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.08);}.algorithm-stage-title {  font-family: Roboto;  font-style: normal;  font-weight: 900;  font-size: 16px;  line-height: 19px;  letter-spacing: -0.2px;  color: #212121;}.algorithm-stage-title.align-middle {  text-align: center;}#algorithm-stage-body {  font-family: Roboto;  font-style: normal;  font-weight: normal;  font-size: 14px;  line-height: 16px;  display: flex;  align-items: center;  letter-spacing: 0.1px;  color: #5a5a65;}#algorithm-stage-add {  background-color: transparent;  border: 2px dashed orange;  border-radius: 8px;}</style>