<template>  <div class="vh-100 d-flex justify-content-center mt-5">    <div class="main-content">      <div class="d-block text-center mb-4">        <img src="@/assets/images/tusd_logo.svg" width="200" alt="TUSD+" />      </div>      <img        src="@/assets/images/landing-page/maintenance.png"        class="img-fluid mb-5"        alt=""      />      <h1 class="text-center mb-5">Coming soon...</h1>      <p v-if="isAuth" class="text-center mt-3">        <router-link          :to="{ name: 'crucial' }"          class="btn btn-dark btn-lg pl-5 pr-5"        >          Back to TUSS D+        </router-link>      </p>      <p v-else class="text-center">        <router-link to="login" class="btn btn-dark btn-lg pl-5 pr-5"          >Login        </router-link>      </p>    </div>  </div></template><script>import axios from 'axios';export default {  name: 'HomePage',  metaInfo: {    title: 'Home page | TUSS D+',  },  data() {    return {      isAuth: false,    };  },  created() {    this.isAuth = this.$store.getters.isAuthenticated;    this.checkTokenExpired();  },  methods: {    checkTokenExpired() {      const token = this.$store.getters.token;      if (token) {        const url = '/api/token/verify/';        const payload = { token: token.substring(7) };        axios.post(url, payload);      }    },  },};</script><style scoped>.main-content {  width: 100%;  max-width: 400px;}</style>