<template>  <div>    <NavBar>      <template #top>        <h6 class="p-3">{{ taskName }}</h6>      </template>    </NavBar>    <SubMenu>      <template #submenu>        <Searchbar :search-items="searchItems" />      </template>    </SubMenu>    <div class="container-fluid">      <p class="text-right">        <router-link class="btn" to="/stake-holder-management"          ><i class="bx bx-chevron-left"></i> Буцах        </router-link>      </p>    </div>  </div></template><script>import NavBar from '@/components/layout/NavBar';import Searchbar from '@/components/layout/SearchBar';import SubMenu from '@/components/layout/SubMenu';export default {  name: 'SingleTask',  components: { NavBar, SubMenu, Searchbar },  layout: 'main',  validate({ params }) {    return /^\d+$/.test(params.id);  },  data() {    return {      taskId: null,      pending: false,      taskName: null,      searchItems: [        { name: 'Employees', icon: 'group' },        { name: 'Ур чадвар', icon: 'book-heart' },        { name: 'Чөлөө', icon: 'edit' },        { name: 'Зөрчил', icon: 'error-circle' },      ],    };  },  head() {    return {      title: 'Ажилтны мэдээлэл | ТУС Д+',    };  },  mounted() {    this.taskId = parseInt(this.$route.params.id);    this.getTaskDetail(this.taskId);    document.body.classList.add('submenu-active');  },  methods: {    async getTaskDetail(taskId) {      await this.$store.dispatch('plan/taskDetail', taskId).then((response) => {        this.pending = false;        this.taskName = response.data.name;      });    },  },};</script>